// Extract question from webhook
const question = $input.first().json.question || $input.first().json.body.question;
const userId = $input.first().json.user_id || $input.first().json.body.user_id || 'anonymous';

console.log('üìù Processing question:', question);
console.log('üë§ User ID:', userId);

// Validate question
if (!question || question.trim() === '') {
  return [{
    json: {
      error: 'Question is required',
      success: false
    }
  }];
}

// Return formatted data
return [{
  json: {
    question: question.trim(),
    user_id: userId,
    timestamp: new Date().toISOString(),
    workflow_step: 'validation'
  }
}];

// Process Langchain response
const langchainResponse = $input.first().json;

console.log('üß† Langchain response received');

// Format response
const formattedResponse = {
  success: langchainResponse.success || true,
  answer: langchainResponse.answer || 'No answer generated',
  sources: langchainResponse.sources || [],
  processing_time: langchainResponse.processing_time || 0,
  method: 'n8n_workflow',
  workflow_step: 'langchain_completed',
  timestamp: new Date().toISOString(),
  disclaimer: '‚öïÔ∏è This is general health information only. Always consult qualified healthcare professionals for medical advice, diagnosis, or treatment.',
  n8n_workflow: {
    workflow_id: $workflow.id,
    execution_id: $execution.id,
    workflow_name: 'Medical QA Workflow'
  }
};

return [{ json: formattedResponse }];

// Error handler
const error = $input.first().json.error || 'Unknown error occurred';

console.error('‚ùå Workflow error:', error);

return [{
  json: {
    success: false,
    answer: 'I apologize, but I encountered an error processing your question. Please try again or consult a medical professional.',
    error: error,
    method: 'n8n_workflow',
    workflow_step: 'error_handler',
    timestamp: new Date().toISOString(),
    n8n_workflow: {
      workflow_id: $workflow.id,
      execution_id: $execution.id
    }
  }
}];
